mapper.select:
SELECT DISTINCT art.article_id as articleId, art.title as title, art.created_at as createdAt, art.updated_at as updatedAt, art.state_flag as stateFlag, tags.tag_id as tagId, tags.tag_name as tagName, feed_cnt_table.feed_cnt as feedCnt, my_cnt_table.my_cnt as myCnt, recommend_cnt_table.recommend_cnt as recommendCnt, users.user_id as userId, users.display_name as displayName, users.photo_url as photoUrl FROM articles as art LEFT OUTER JOIN qiita_builder.articles_tags_relations as a_t_relation ON art.article_id = a_t_relation.article_id LEFT OUTER JOIN qiita_builder.tags as tags ON a_t_relation.tag_id = tags.tag_id LEFT OUTER JOIN (SELECT article_id, count(feed.article_id) as feed_cnt FROM qiita_builder.feedbacks as feed GROUP BY feed.article_id) as feed_cnt_table ON art.article_id = feed_cnt_table.article_id LEFT OUTER JOIN (SELECT article_id, count(my.article_id) as my_cnt FROM qiita_builder.my_articles as my GROUP BY my.article_id) as my_cnt_table ON art.article_id = my_cnt_table.article_id LEFT OUTER JOIN (SELECT article_id, count(recommend.article_id) as recommend_cnt FROM qiita_builder.qiita_recommends as recommend GROUP BY recommend.article_id) as recommend_cnt_table ON art.article_id = recommend_cnt_table.article_id LEFT OUTER JOIN qiita_builder.users as users ON art.user_id = users.user_id WHERE art.article_id in
#{articleId}
ORDER BY ${search.sort} DESC

SELECT art.article_id, created_at as createdAt, updated_at as updatedAt, feed_cnt_table.feed_cnt as feedCnt, my_cnt_table.my_cnt as myCnt, recommend_cnt_table.recommend_cnt as recommendCnt FROM articles as art
JOIN (SELECT article_id as atArticleId FROM qiita_builder.articles_tags_relations where tag_id IN
#{tag}
GROUP BY atArticleId ) as a_t_relation ON art.article_id = a_t_relation.atArticleId
JOIN (SELECT users.user_id FROM qiita_builder.users
display_name LIKE CONCAT('%', #{search.searchWord}, '%')
AND user_id = #{search.userId}
) as users ON art.user_id = users.user_id
LEFT OUTER JOIN (SELECT article_id, COUNT(feed.article_id) as feed_cnt FROM qiita_builder.feedbacks as feed GROUP BY feed.article_id) as feed_cnt_table ON art.article_id = feed_cnt_table.article_id LEFT OUTER JOIN (SELECT article_id, COUNT(my.article_id) as my_cnt FROM qiita_builder.my_articles as my GROUP BY my.article_id) as my_cnt_table on art.article_id = my_cnt_table.article_id LEFT OUTER JOIN (SELECT article_id, COUNT(recommend.article_id) as recommend_cnt FROM qiita_builder.qiita_recommends as recommend GROUP BY recommend.article_id) as recommend_cnt_table ON art.article_id = recommend_cnt_table.article_id
state_flag IN
#{stateFlag}
AND title LIKE CONCAT('%', #{search.searchWord}, '%')
AND created_at between NOW() - interval 7 day and NOW()
AND DATE_FORMAT(created_at, '%Y%m') = DATE_FORMAT(NOW(), '%Y%m')
order by ${search.sort} desc
LIMIT #{search.pageSize} OFFSET #{search.offset}

SELECT article_id
FROM articles
WHERE user_id = #{userId}
  AND (state_flag = 1 OR state_flag = 2)
ORDER BY created_at DESC;

SELECT qiita_article_id
FROM articles
WHERE article_id = #{articleId}

SELECT articles.article_id as article_id, articles.user_id as user_id, articles.content as content, articles.created_at as created_at, articles.updated_at as updated_at, articles.title as title, articles.content as content, articles.qiita_article_id as qiita_article_id, articles.state_flag as state_flag,
tags.tag_id as tag_id, tags.tag_name as tag_name
FROM articles LEFT JOIN articles_tags_relations as atr ON articles.article_id = atr.article_id LEFT JOIN tags ON tags.tag_id = atr.tag_id WHERE articles.article_id = #{articleId};

mapper.update:
UPDATE articles
SET user_id          = #{postedUser.userId},
    updated_at       = NOW(),
    title            = #{title},
    content          = #{content},
    qiita_article_id = #{qiitaArticleId},
    state_flag       = #{stateFlag},
    qiita_article_id = #{qiitaArticleId}
WHERE article_id = #{articleId}
  AND user_id = #{postedUser.userId}
