mapper.select:
# sortId = # 1 : FB投稿数Rank # 2 : 記事投稿数Rank
SELECT ( select qr.article_id from qiita_recommends AS qr WHERE qr.article_id IN (
SELECT fb.article_id FROM feedbacks AS fb WHERE fb.user_id = #{userId} AND fb.article_id IN ( SELECT art.article_id FROM articles AS art WHERE art.state_flag = 1 OR art.state_flag = 2 )
SELECT art.article_id FROM articles AS art WHERE art.user_id = #{userId} AND (state_flag = 1 OR state_flag = 2)
SELECT art.article_id FROM articles AS art WHERE art.user_id = #{userId} AND (state_flag = 1 OR state_flag = 2)
) group by qr.article_id having count(*) = max(result.cnt) ) AS max_count_article_id FROM ( SELECT article_id, count(*) AS cnt FROM qiita_recommends WHERE article_id IN (
SELECT fb.article_id FROM feedbacks AS fb WHERE fb.user_id = #{userId} AND fb.article_id IN ( SELECT art.article_id FROM articles AS art WHERE art.state_flag = 1 OR art.state_flag = 2 )
SELECT article_id FROM articles WHERE user_id = #{userId} AND (state_flag = 1 OR state_flag = 2)
SELECT article_id FROM articles WHERE user_id = #{userId} AND (state_flag = 1 OR state_flag = 2)
) GROUP BY article_id ) AS result;

SELECT *
FROM qiita_recommends
WHERE article_id = #{articleId}
  AND recommend_user_id = #{recommendUserId};
